shader.vert:
#version 300 es

uniform float uniTime;
uniform vec2 uniAspectRatioContain;
in vec2 attPoint;
in vec3 attColor;
in float attMirror;
out vec3 varColor;

void main() {
    float t = uniTime + attPoint.x * 1000.0;
    float strength = 0.9 + 0.5 * cos(t * 0.002);
    varColor = attColor * strength;
    gl_Position = vec4(
        attPoint * uniAspectRatioContain * vec2(attMirror, 1.0), 
        0.0, 1.0
    );    
}

/**
 * out vec4 gl_Position
 * out float gl_PointSize
 * out float gl_ClipDistance[] 
 * in int gl_VertexID
 * in int gl_InstanceID
 */

shader.frag:
#version 300 es

precision mediump float;

in vec3 varColor;
out vec4 FragColor;

void main() {
    FragColor = vec4(varColor, 1.0);
}

